import React, { useMemo } from 'react'
import SvgStartIcon from '../common/SvgStarIcon';
import SvgEmptyStartIcon from '../common/SvgStarIcon';
const Rating = ({rating}) => {
    const ratingNumber = useMemo(()=>{
        return Array(Math.floor(Number(rating))).fill()
    }, [rating])
  return (
    <div className='flex items-center'>
        {ratingNumber?.map((_,index)=>{
            <SvgStartIcon key={index}/>
        })}
        {
            new Array(5-ratingNumber?.length).fill().map((_,index)=>(
                <SvgEmptyStartIcon key={'empty' + index}/>
            ))
        }
    </div>
  )
}

export default Rating
