import React from 'react'
// import ArrowIcon from '../common/ArrowIcon'
import Jeans from '../../assets/images/jeans.jpg'

const Card = ({imagePath, title}) => {
  return (
    <div className='flex  flex-col p-8'>
      <img src={imagePath} className='max-h[260px] max-w-[240px] min-h-[260px] min-w-[240px] 
      bg-cover bg-center border rounded hover:scale-105 cursor-pointer' alt='Jeans'/>
      <p className='text-[16px] p-[5px]'>{title}</p>
    </div>
  )
}

export default Card
